前端开发面试 - 编程题 Function.prototype.bind() //  Yes, it does work with `new (funcA.bind(thisArg, args))`
if (!Function.prototype.bind) (function () {
  var ArrayPrototypeSlice = Array.prototype.slice;
  Function.prototype.bind = function (otherThis) {
    if (typeof this !== 'function') {
      // closest thing possible to the ECMAScript 5
      // internal IsCallable function
      throw new TypeError('Function.prototype.bind - what is trying to be bound is not callable');
    }

    var baseArgs = ArrayPrototypeSlice.call(arguments, 1),
      baseArgsLength = baseArgs.length,
      fToBind = this,
      fNOP = function () { },
      fBound = function () {
        baseArgs.length = baseArgsLength; // reset to default base arguments
        baseArgs.push.apply(baseArgs, arguments);
        return fToBind.apply(
          fNOP.prototype.isPrototypeOf(this) ? this : otherThis, baseArgs
        );
      };

    if (this.prototype) {
      // Function.prototype doesn't have a prototype property
      fNOP.prototype = this.prototype;
    }
    fBound.prototype = new fNOP();

    return fBound;
  };
})(); debounce  和  throttle 函数防抖和函数节流（debounce and throttle） JavaScript 专题之跟着 underscore 学防抖 JavaScript 专题之跟着 underscore 学节流 debounce function debounce(fn,delay,immediate) {
  let timer;
  return function () {
    let that = this;
    let args = arguments;
    if (timer) clearTimeout(timer);
    if (immediate) { // 这里设置是否启用立即执行
      let able = !timer;
      timer = setTimeout(() => {
        timer = null;
      }, delay)
      if (able) fn.apply(that, args)
    } else {
      timer = setTimeout(()=>{
        fn.apply(that, args)
      }, delay);
    }
  }
} throttle function throttle(fn, delay, immediate) {
  let timer;
  return function() {
    let that = this;
    let args = arguments;
    if (!timer) {
      if(immediate){
        fn.apply(that, args)
        timer = setTimeout(() => {
          timer = null;
        }, delay)
      }else {
        timer = setTimeout(() => {
          timer = null;
          fn.apply(that, args)
        }, delay)
      }
    }
  }
